from PyQt4.QtCore import *from PyQt4.QtGui import *from ManagerMenuBar import *import sysclass Manager_Main_Menu(QMainWindow):    """Main Menu for use by managers"""    def __init__(self):        super().__init__()        self.setWindowTitle("Manager Main Menu")        self.horizontal_layout = QHBoxLayout()        self.DeparmentInfoBtn = QPushButton("Department Information")        self.MyInfoBtn = QPushButton("My Information")        self.horizontal_layout.addWidget(self.DeparmentInfoBtn)        self.horizontal_layout.addWidget(self.MyInfoBtn)        self.btnWidget = QWidget()        self.btnWidget.setLayout(self.horizontal_layout)        self.setCentralWidget(self.btnWidget)        def main():    app = QApplication(sys.argv)    launcher = Manager_Main_Menu()    launcher.show()    launcher.resize(400,200)    launcher.raise_()    app.exec_()    if __name__ == "__main__":    main()