from PyQt4.QtCore import *from PyQt4.QtGui import *from ManagerMenuBar import *import sysclass Manager_Main_Menu(QWidget):    """Main Menu for use by managers"""    def __init__(self):        super().__init__()            self.setWindowTitle("Manager Main Menu")        self.Vertical_Layout = QVBoxLayout()                self.DeparmentInfoBtn = QPushButton("Department Information")        self.DeparmentInfoBtn.setFont(QFont("Calibri",20))        self.MyInfoBtn = QPushButton("My Information")        self.MyInfoBtn.setFont(QFont("Calibri",20))        self.DeparmentInfoBtn.setFixedHeight(100)        self.MyInfoBtn.setFixedHeight(100)        self.Vertical_Layout.addWidget(self.DeparmentInfoBtn)        self.Vertical_Layout.addWidget(self.MyInfoBtn)        self.setLayout(self.Vertical_Layout)def main():    app = QApplication(sys.argv)    launcher = Manager_Main_Menu()    launcher.show()    launcher.resize(400,200)    launcher.raise_()    app.exec_()    if __name__ == "__main__":    main()