from PyQt4.QtCore import *from PyQt4.QtGui import *from ManagerMenuBar import *import sysclass Manager_Main_Menu(QWidget):    """Main Menu for use by managers"""    def __init__(self):        super().__init__()        self.ButtonStyleSheet =  ("""QPushButton{                    color: #333;                    border: 2px solid #555;                    border-radius: 11px;                    padding: 5px;                    background: qradialgradient(cx: 0.3, cy: -0.4,                    fx: 0.3, fy: -0.4,                    radius: 1.35, stop: 0 #fff, stop: 1 #888);                    min-width: 80px;                    }"""                                                 """QPushButton:hover{                    background: qradialgradient(cx: 0.3, cy: -0.4,                    fx: 0.3, fy: -0.4,                    radius: 1.35, stop: 0 #fff, stop: 1 #bbb);                    }"""                    """QPushButton:pressed {                    background: qradialgradient(cx: 0.4, cy: -0.1,                    fx: 0.4, fy: -0.1,                    radius: 1.35, stop: 0 #fff, stop: 1 #ddd);                    }""")        self.setWindowTitle("Manager Main Menu")        self.Vertical_Layout = QVBoxLayout()                self.DeparmentInfoBtn = QPushButton("Department Information")        self.MyInfoBtn = QPushButton("My Information")        self.DeparmentInfoBtn.setFixedHeight(100)        self.MyInfoBtn.setFixedHeight(100)        self.DeparmentInfoBtn.setStyleSheet(self.ButtonStyleSheet)        self.MyInfoBtn.setStyleSheet(self.ButtonStyleSheet)        self.MyInfoBtn.setFont(QFont("Calibri",20))        self.DeparmentInfoBtn.setFont(QFont("Calibri",20))        self.Vertical_Layout.addWidget(self.DeparmentInfoBtn)        self.Vertical_Layout.addWidget(self.MyInfoBtn)        self.setLayout(self.Vertical_Layout)def main():    app = QApplication(sys.argv)    launcher = Manager_Main_Menu()    launcher.show()    launcher.resize(400,200)    launcher.raise_()    app.exec_()    if __name__ == "__main__":    main()